+void drawMenu(MenuItem *items, int count) {
+    tft.fillScreen(0x10A2);
+    tft.setTextSize(2);
+    tft.setFont(&FreeSansBold9pt7b);
+    
+    for(int i = 0; i < count; i++) {
+        tft.setCursor(30, 60 + (i * 30));
+        tft.setTextColor(i == settingsSelectedOption ? 0xFD40 : 0xDED9);
+        tft.print(items[i].name);
+    }
